class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}newCommentDom(e){return $(`<div id="comment-${e._id}" class="comment">\n\n        <img style="width: 32px;" src="https://res.cloudinary.com/dilxqj69w/image/upload/v1627114103/5336880_oygcgf.png" alt="comment-user-photo">\n        &ensp;<span>${e.user.name}</span>\n    \n        <div class="comment-content">\n         ${e.content}                          \n        </div>\n    \n        <a class="delete-comment-button" style="color:rgb(231, 128, 128); margin-left: 10px;" href="/comments/destroy/${e._id}"><i class="far fa-times-circle"></i></a>\n\n        \x3c!--show the count of zero likes on this dynamically created comment --\x3e\n        <br>\n        <small>               \n            <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                0 Likes\n            </a>\n         </small>\n    </div>`)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}}